       if ($orderby=='ASC') { $order = 'ASC'; } else { $order = 'DESC'; } 
       $sql = "SELECT AttributeDefID, concat(IFNULL(TimestampCreated,'') || ' ' || IFNULL(TimestampModified,'') || ' ' || IFNULL(Version,'') || ' ' || IFNULL(DataType,'') || ' ' || IFNULL(FieldName,'') || ' ' || IFNULL(TableType,'') || ' ' || IFNULL(PrepTypeID,'') || ' ' || IFNULL(ModifiedByAgentID,'') || ' ' || IFNULL(DisciplineID,'') || ' ' || IFNULL(CreatedByAgentID,'')) FROM attributedef order by PrepTypeID, ModifiedByAgentID, DisciplineID, CreatedByAgentID $order ";
       $comma = '';
       if ($result = $connection->query($sql)) { 
          while ($row = $result->fetch_row()) {
             if ($row) {
                $pkval = trim($row[0]);
                $fval = trim($row[1]);
                if ($pkval!='') { 
                    $pkval = str_replace('"','&quot;',$pkval);
