   public function keySelectAllConcatJSON($orderby='ASC') {
        // ******* Note: $connection must be a mysqli object.
        global $connection;
       $returnvalue = '';
       $order = '';
       if ($orderby=='ASC') { $order = 'ASC'; } else { $order = 'DESC'; } 
       $sql = "SELECT JournalID, concat(IFNULL(TimestampCreated,'') || ' ' || IFNULL(TimestampModified,'') || ' ' || IFNULL(Version,'') || ' ' || IFNULL(GUID,'') || ' ' || IFNULL(ISSN,'') || ' ' || IFNULL(JournalAbbreviation,'') || ' ' || IFNULL(JournalName,'') || ' ' || IFNULL(Remarks,'') || ' ' || IFNULL(Text1,'') || ' ' || IFNULL(ModifiedByAgentID,'') || ' ' || IFNULL(CreatedByAgentID,'')) FROM journal order by GUID, JournalName, ModifiedByAgentID, CreatedByAgentID $order ";
       $comma = '';
       if ($result = $connection->query($sql)) { 
          while ($row = $result->fetch_row()) {
             if ($row) {
                $pkval = trim($row[0]);
                $fval = trim($row[1]);
                if ($pkval!='') { 
                    $pkval = str_replace('"','&quot;',$pkval);
