          for ($i=0;$i<count($ids);$i++) {
              $obj = new huh_lithostrat();
              $obj->load($ids[$i]);
              $returnvalue[] = $obj;
              $result=true;
          }
          if ($result===false) { $this->error = mysqli_error($connection); }
       }
