   public function keySelectAllConcatJSON($orderby='ASC') {
        // ******* Note: $connection must be a mysqli object.
        global $connection;
       $returnvalue = '';
       $order = '';
       if ($orderby=='ASC') { $order = 'ASC'; } else { $order = 'DESC'; } 
       $sql = "SELECT OtherIdentifierID, concat(IFNULL(TimestampCreated,'') || ' ' || IFNULL(TimestampModified,'') || ' ' || IFNULL(Version,'') || ' ' || IFNULL(CollectionMemberID,'') || ' ' || IFNULL(Identifier,'') || ' ' || IFNULL(Institution,'') || ' ' || IFNULL(Remarks,'') || ' ' || IFNULL(ModifiedByAgentID,'') || ' ' || IFNULL(CreatedByAgentID,'') || ' ' || IFNULL(CollectionObjectID,'')) FROM otheridentifier order by CollectionMemberID, ModifiedByAgentID, CreatedByAgentID, CollectionObjectID $order ";
       $comma = '';
       if ($result = $connection->query($sql)) { 
          while ($row = $result->fetch_row()) {
             if ($row) {
                $pkval = trim($row[0]);
                $fval = trim($row[1]);
                if ($pkval!='') { 
                    $pkval = str_replace('"','&quot;',$pkval);
