       $returnvalue .= "<li>ISLOANABLE<input type=text name=".huh_preptype::ISLOANABLE." id=".huh_preptype::ISLOANABLE." value='".$model->getIsLoanable()."'  size='".huh_preptype::ISLOANABLE_SIZE ."'  maxlength='".huh_preptype::ISLOANABLE_SIZE ."' ></li>\n";
       $returnvalue .= "<li>NAME<input type=text name=".huh_preptype::NAME." id=".huh_preptype::NAME." value='".$model->getName()."'  size='51'  maxlength='".huh_preptype::NAME_SIZE ."' ></li>\n";
       $returnvalue .= "<li>COLLECTIONID<input type=text name=".huh_preptype::COLLECTIONID." id=".huh_preptype::COLLECTIONID." value='".$model->getCollectionID()."'  size='".huh_preptype::COLLECTIONID_SIZE ."'  maxlength='".huh_preptype::COLLECTIONID_SIZE ."' ></li>\n";
       $returnvalue .= "<li>MODIFIEDBYAGENTID<input type=text name=".huh_preptype::MODIFIEDBYAGENTID." id=".huh_preptype::MODIFIEDBYAGENTID." value='".$model->getModifiedByAgentID()."'  size='".huh_preptype::MODIFIEDBYAGENTID_SIZE ."'  maxlength='".huh_preptype::MODIFIEDBYAGENTID_SIZE ."' ></li>\n";
       $returnvalue .= "<li>CREATEDBYAGENTID<input type=text name=".huh_preptype::CREATEDBYAGENTID." id=".huh_preptype::CREATEDBYAGENTID." value='".$model->getCreatedByAgentID()."'  size='".huh_preptype::CREATEDBYAGENTID_SIZE ."'  maxlength='".huh_preptype::CREATEDBYAGENTID_SIZE ."' ></li>\n";
       if ($includeRelated) { 
           // note that $includeRelated is provided as false in calls out to
           // related tables to prevent infinite loops between related objects.
        }
       $returnvalue .= '<li><input type=submit value="Save"></li>';
       $returnvalue .= '</ul>';
       $returnvalue .= '</form>';
       return  $returnvalue;
   }
