   // wrapped as $values in: '{ "identifier":"id", "items": [ '.$values.' ] }';
   // when druid_handler.php is called with druid_action=returnfkjson
   // Used with dojo dijit.form.FilteringSelect to submit surrogate numeric key values from a text picklist 
   public function keySelectAllConcatJSON($orderby='ASC') {
        // ******* Note: $connection must be a mysqli object.
        global $connection;
       $returnvalue = '';
       $order = '';
       if ($orderby=='ASC') { $order = 'ASC'; } else { $order = 'DESC'; } 
       $sql = "SELECT SpReportId, concat(IFNULL(TimestampCreated,'') || ' ' || IFNULL(TimestampModified,'') || ' ' || IFNULL(Version,'') || ' ' || IFNULL(Name,'') || ' ' || IFNULL(Remarks,'') || ' ' || IFNULL(RepeatCount,'') || ' ' || IFNULL(RepeatField,'') || ' ' || IFNULL(CreatedByAgentID,'') || ' ' || IFNULL(SpecifyUserID,'') || ' ' || IFNULL(AppResourceID,'') || ' ' || IFNULL(ModifiedByAgentID,'') || ' ' || IFNULL(WorkbenchTemplateID,'') || ' ' || IFNULL(SpQueryID,'')) FROM spreport order by Name, CreatedByAgentID, SpecifyUserID, AppResourceID, ModifiedByAgentID, WorkbenchTemplateID, SpQueryID $order ";
       $comma = '';
       if ($result = $connection->query($sql)) { 
          while ($row = $result->fetch_row()) {
             if ($row) {
                $pkval = trim($row[0]);
                $fval = trim($row[1]);
                if ($pkval!='') { 
                    $pkval = str_replace('"','&quot;',$pkval);
                    $fval = str_replace('"','&quot;',$fval);
                    $returnvalue .= $comma . ' { "id":"'.$pkval.'", "fields": "'.$fval.'" } ';
                    $comma = ', ';
                }
             }
          }
       } else { 
          $this->error = mysqli_error(); 
       }
       $result->close();
       return $returnvalue;
   }
