       $returnvalue .= "<li>MODIFIEDBYAGENTID<input type=text name=".huh_storagetreedef::MODIFIEDBYAGENTID." id=".huh_storagetreedef::MODIFIEDBYAGENTID." value='".$model->getModifiedByAgentID()."'  size='".huh_storagetreedef::MODIFIEDBYAGENTID_SIZE ."'  maxlength='".huh_storagetreedef::MODIFIEDBYAGENTID_SIZE ."' ></li>\n";
       $returnvalue .= "<li>CREATEDBYAGENTID<input type=text name=".huh_storagetreedef::CREATEDBYAGENTID." id=".huh_storagetreedef::CREATEDBYAGENTID." value='".$model->getCreatedByAgentID()."'  size='".huh_storagetreedef::CREATEDBYAGENTID_SIZE ."'  maxlength='".huh_storagetreedef::CREATEDBYAGENTID_SIZE ."' ></li>\n";
       if ($includeRelated) { 
           // note that $includeRelated is provided as false in calls out to
           // related tables to prevent infinite loops between related objects.
        }
       $returnvalue .= '<li><input type=submit value="Save"></li>';
       $returnvalue .= '</ul>';
       $returnvalue .= '</form>';
       return  $returnvalue;
   }
}

