   public function keySelectDistinct($fieldname,$startline,$link,$endline,$includecount=false,$orderbycount=false) {
       $returnvalue = '';
       switch ($fieldname) { 
          case 'RowNumber':
             $returnvalue = $this->selectDistinctRowNumber($startline,$link,$endline,$includecount,$orderbycount);
